import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './HomePage.scss'; // You can style this component using CSS
import Banner from '../assets/Supr-Skin-facebook-cover-double.jpg';
import Logo from '../assets/SuprSkin.png';
import axios from 'axios'
import CategoryIcon from './CategoryIcon';

const allRegimens = [
  {
    title: 'Ashwaghanda Regimen',
    products: ['Ashwaghanda Serum', 'Ashwaghanda Scrub', 'Ashwaghanda Tea']
  },
  {
    title: 'Gotukola Regimen',
    products: ['Gotukola Serum', 'Gotukola Scrub', 'Gotukola Tea']
  }
];

function Regimen() {
  const [regimens, setRegimens] = useState();
  const [regimenQuestion, setRegimenQuestion] = useState(0);
  const [answers, setAnswers] = useState({
    question1: '',
    question2: '',
    question3: '',
    question4: ''
  });

  useEffect(() => {
    axios
      .get('http://localhost:8001/regimens')
      .then((res) => {
        console.log(res.data)
        setRegimens(res.data)
      })
      .catch((error) => {
        console.error('Error fetching regimens:', error);
      });
  }, [])

  const handleQuestionClick = (regimenQuestion) => {
    setQuestionAnswers(prevAnswers => ({...prevAnswers,
    [regimenQuestion]: "Selected answer here" // Set the selected answer value
  }));

    setRegimenQuestion(prev => prev + 1)
  }

  const handleAnswerChange = (question, answer) => {
    setAnswers(prevAnswers => ({
      ...prevAnswers,
      [question]: answer
    }));
  };

  const handleSubmit = e => {
    e.preventDefault();
    // Process the answers
    console.log('Answers:', answers);
  };

  return (
    <div className="main-page">
      <div className='title-holder'>
        <h4>Regimen Finder</h4>
      </div>
      <div className="banner-holder-2">
        <img src={Banner} alt="SuprSkin banner" className='banner' />
        <div className="center-overlay">
          {regimenQuestion === 0
            ?
            <div className='regimen-finder'>
              <div className='question'>
                <p>Question 1: What is your favorite color?</p>
                <label>
                  <input
                    type='radio'
                    name='question1'
                    value='red'
                    checked={answers.question1 === 'red'}
                    onChange={e => handleAnswerChange('question1', e.target.value)}
                  />
                  Red
                </label>
                <label>
                  <input
                    type='radio'
                    name='question1'
                    value='blue'
                    checked={answers.question1 === 'blue'}
                    onChange={e => handleAnswerChange('question1', e.target.value)}
                  />
                  Blue
                </label>
                <p onClick={handleQuestionClick}>next</p>
              </div>
            </div>
            :
            <p onClick={handleQuestionClick}>Find My Regimen</p>
          }
        </div>
      </div>
      {regimens && (
        <div className='categories-holder'>
          {regimens.regimens.map(regimen => (
            <CategoryIcon key={regimen.id} title={regimen.regimen_title} />
          ))}
        </div>
      )}
    </div>
  );
}

export default Regimen;


